//trackThis.js
//http://github.com/jonathanhaslett/trackThis.js
function trackThis(){$('a:not([href^="tel:"]).trackThis').each(function(){$(this).click(function(){var e=$(this).attr("data-trackThis").split(",");_gaq.push(["_trackEvent",e[0],e[1],e[2],parseInt(e[3],10)]);return!0})});$('a[href^="tel:"].trackThis').each(function(){if($("html").hasClass("no-touch")){var e=$(this).html();$(this).html(e.substring(0,e.length-3)+"...");$(this).click(function(){var t=$(this).attr("data-trackThis").split(",");if($(this).hasClass("clicked")){_gaq.push(["_trackEvent",t[0],"Clicked",t[2],0]);return!0}$(this).addClass("clicked");$(this).html(e);_gaq.push(["_trackEvent",t[0],"Revealed",t[2],parseInt(t[3],10)]);return!1})}else $(this).click(function(){var e=$(this).attr("data-trackThis").split(",");_gaq.push(["_trackEvent",e[0],e[1],e[2],parseInt(e[3],10)]);return!0})})};