//http://github.com/jonathanhaslett/trackThis.js
function trackThis(){$('a[href^="tel:"]').on("click",function(e){var t=$(this).attr("href");_gaq.push(["_trackEvent","Telephone","Clicked",t],function(){return!0})});$('a[href^="mailto:"]').on("click",function(e){var t=$(this).attr("href");_gaq.push(["_trackEvent","Email","Clicked",t],function(){return!0})});$('a[href^="#"]').on("click",function(e){var t=$(this).attr("href");_gaq.push(["_trackPageview",$(location).attr("pathname")+t],function(){return!0})});$('a[href*="://"]:not([href*="'+location.hostname.replace("www.","")+'"])').on("click",function(e){var t=$(this).attr("href");$(this).attr("target","_blank").attr("rel","external");_gaq.push(["_trackEvent","External Link","Clicked",t],function(){return!0})});$('a[href$=".pdf"],a[href$=".zip"],a[href$=".dmg"]').on("click",function(e){var t=$(this).attr("href");$(this).attr("target","_blank").attr("rel","external");_gaq.push(["_trackEvent","File","Downloaded",t],function(){return!0})})}trackThis();